function OurValueCard({ svg, title, subtitle }: any) {
  return (
    <div data-aos="fade-up" className="our__values__card">
      <div className="our__values__card__col">{svg}</div>
      <div className="our__values__card__col">
        <div data-aos="fade-up" className="our__values__card__col__title">
          {title}
        </div>
        <div data-aos="fade-up" className="our__values__card__col__subtitle">
          {subtitle}
        </div>
      </div>
    </div>
  );
}
